import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import "../components/Styles/LoginAccount.css"; // Import the correct CSS file

function ProfessionalLogin() {
  // State variables to store user input and any error messages
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const navigate = useNavigate();

  // Handle form submission
  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      // Send a POST request to the backend for login
      const response = await axios.post('http://localhost:8080/api/v1/auth/login', {
        email,
        password,
      });

      // Check if the response contains a valid user and userType
      const { userType, message } = response.data;
      if (userType === "BoardingOwner") {
        setErrorMessage("");
        navigate("/ProfDashboard"); // Redirect to professional dashboard
      } else if (userType === "Student") {
        setErrorMessage("Access denied: Students cannot access this page.");
        setTimeout(() => {
          setErrorMessage("");
        }, 1000);
        // You can redirect them to the student dashboard or show a custom message
        navigate("/StudentLogin");
      } else {
        setErrorMessage("Invalid user type.");
        setTimeout(() => {
          setErrorMessage("");
        }, 1000);
      }

      // Logging the response message (you can use this for further success message handling)
      console.log(message);

    } catch (error) {
      // If there is an error (e.g., invalid credentials), display the error message
      setErrorMessage(error.response ? error.response.data.message : "Something went wrong!");
      setTimeout(() => {
        setErrorMessage("");
      }, 1000);
    }
  };

  return (
    <div className="form-container">
      <h1>Professional Login</h1>
      <form onSubmit={handleLogin}>
        <div className="form-group">
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div className="form-group">
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <button className="btn" type="submit">Sign In</button>
      </form>
      {errorMessage && <div style={{ color: 'red', marginTop: '10px' }}>{errorMessage}</div>}
      <div style={{ marginTop: "20px" }}>
        <p>Donâ€™t have an account?</p>
        <Link to="/create-Professional-account">
          <button className="btn">Create Professional Account</button>
        </Link>
        <p></p>
        <Link to="/">
          <button className="btn">Go Back</button>
        </Link>
      </div>
    </div>
  );
}

export default ProfessionalLogin;
